module.property-provider:
  	{
	    var userAgent = window.navigator.userAgent.toLowerCase();
	    if (userAgent.indexOf('android') != -1) {
	      return "android"
	    }

	    if(userAgent.indexOf('ipad') != -1 || userAgent.indexOf('ipod') != -1  || userAgent.indexOf('iphone') != -1) {
	      return "ios"
	    }

	    return "browser";
  	}
